query:
UPDATE courses 
SET description = 'Embarque em uma jornada profunda de autodescoberta e expansão emocional. Este programa estruturado guia você através de práticas e reflexões que fortalecem sua inteligência emocional, ampliam sua consciência e desenvolvem ferramentas para uma vida mais plena e autêntica.'
WHERE slug = 'autoconhecimento-inteligencia-emocional';

UPDATE lessons 
SET description = 'Inicie sua jornada compreendendo os fundamentos da evolução consciente. Descubra como o autoconhecimento é a base para todas as transformações significativas em sua vida.'
WHERE title = 'Introdução ao Autoconhecimento';

UPDATE lessons 
SET description = 'Explore as dimensões da inteligência emocional e como ela influencia todas as áreas da sua existência. Aprenda a reconhecer, compreender e navegar suas emoções com maestria.'
WHERE title = 'Fundamentos da Inteligência Emocional';

UPDATE lessons 
SET description = 'Desenvolva a habilidade de observar seus pensamentos e emoções sem julgamento. Cultive presença e consciência plena para decisões mais alinhadas com seu verdadeiro eu.'
WHERE title = 'Consciência Emocional e Mindfulness';

UPDATE lessons 
SET description = 'Aprenda estratégias elegantes para transformar padrões emocionais limitantes. Desenvolva resiliência e equilíbrio emocional duradouros através de práticas estruturadas.'
WHERE title = 'Gestão Emocional Avançada';

UPDATE lessons 
SET description = 'Integre todos os aprendizados em um plano personalizado de evolução contínua. Estabeleça práticas sustentáveis para manter sua expansão consciente ao longo da vida.'
WHERE title = 'Integração e Prática Diária';

